import "./main.css";

import React, { useState } from 'react';
import axios from 'axios';

function ChatApp() {
  const [inputMessage, setInputMessage] = useState('');
  const [messages, setMessages] = useState([]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (inputMessage.trim()) {
      // Сохраняем введенное пользователем сообщение на экране
      setMessages(prevMessages => [...prevMessages, inputMessage]);

      try {
        // Отправляем сообщение на бэкенд
        const response = await axios.post('http://localhost:8000/api/messages/', {
          message: inputMessage
        });

        // Получаем обработанное сообщение от бэкенда
        const processedMessage = response.data.processed_message;

        // Добавляем обработанное сообщение на экран
        setMessages(prevMessages => [...prevMessages, processedMessage]);
      } catch (error) {
        console.error('Error sending message', error);
      }

      // Очищаем поле ввода
      setInputMessage('');
    }
  };

  return (
    <div className="App">
      <div className="messenger">
        <div className="messages">
          {messages.length > 0 ? (
            messages.map((msg, index) => <div key={index}>{msg}</div>)
          ) : (
            <div>нет сообщений</div>
          )}
        </div>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Введите сообщение"
            onKeyPress={(e) => { if (e.key === 'Enter') handleSubmit(e); }}
          />
          <button type="submit">Отправить</button>
        </form>
      </div>
    </div>
  );
}

export default ChatApp;
