import "./main.css";

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatApp() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Получаем все сообщения при загрузке страницы
  useEffect(() => {
    fetchMessages();
  }, []);

  async function fetchMessages() {
    try {
      const response = await axios.get('http://localhost:8000/api/messages/');
      setMessages(response.data);
    } catch (error) {
      console.error(error);
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();
    setIsLoading(true);
    setNewMessage('');
    try {
      const response = await axios.post(
        'http://localhost:8000/api/messages/',
        { text: newMessage },
        { headers: { 'Content-Type': 'application/json' } }
      );
      
      setIsLoading(false);
      setMessages([...messages, { text: newMessage, processed_text: response.data.processed_text }]);
      
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  }
  const highlightText = (processed_text) => {
    // Разбиваем строку по символам ```
    const parts = processed_text.split('```');

    // Формируем массив элементов для рендеринга
    const elements = [];
    for (let i = 0; i < parts.length; i++) {
      let part = parts[i];
      
      // Выделяем первое слово каждого блока текста
      if (i % 2 === 0) {
        const words = part.split(' ');
        if (words.length > 0) {
          elements.push(<strong key={`first-word-${i}`}>{words.shift()}</strong>);
          elements.push(words.join(' '));
        }
      } else {
        // Текст между ``` выделяем как код
        elements.push(
          <code key={`code-block-${i}`}>
            {part}
          </code>
        );
      }
    }}

  return (
    <div>
      <h1>Мессенджер</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Введите сообщение..."
        />
        <button type="submit">Отправить</button>
        {isLoading && <span>Загрузка...</span>}
      </form>
      {messages.length === 0 ? (
        <p>Нет сообщений</p>
        
      ) : (
        messages.map((message, index) => (
          
          <div key={index}>
            <p>{message.text}</p>
            <div className="highlighted-text">
            <p style={{ color: 'green' }}>{highlightText(message.processed_text)}</p>
            </div>
          </div>
        ))
      )}
    </div>
  );
}

export default ChatApp;
