import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./main.css";

const ChatApp = () => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Загрузить существующие сообщения при инициализации
    loadMessages();
  }, []);

  const loadMessages = async () => {
    try {
      const response = await axios.get('http://localhost:8000/api/messages/');
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (message.trim() === '') return;

    const newMessage = { text: message, loading: true };
    setMessages(prev => [...prev, { original_text: newMessage.text, processed_text: newMessage.text }]);

    try {
      const response = await axios.post('http://localhost:8000/api/messages/', newMessage);
      setMessages(prev => [...prev.slice(0, -1), response.data]);
      setMessages((prevMessages) =>
        prevMessages.map((msg, index) =>
          index === prevMessages.length - 1 ? { text: message, loading: false } : msg
        )
      );
    } catch (error) {
      console.error('Error sending message:', error);
    } finally {
      setMessage('');
    }
  };

  return (
    <div>
      <h1>Messenger</h1>
      <div>
        {messages.length > 0 ? (
          messages.map((msg, index) => (
            
            <div key={index}>{msg.loading ? 'загрузка...' : msg.processed_text}{msg.original_text}</div>
          ))
        ) : (
            <div className='nomessage'>
            <div className="container-nomessage">
                <div className="maintext-nomessage">
                Сообщений пока нет...
                </div>
                <div className="text-nomessage">
                  Задайте мне любой вопрос и я постараюсь дать вам ответ
                </div>
                <div className='img-nomessage'>
                  <img className='gif-nomessage' src="https://i.yapx.cc/YQuRO.gif" alt="" /> 
                </div>
            </div>
            </div>
        )}
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSubmit(e)}
        />
        <button type="submit">Отправить</button>
      </form>
    </div>
  );
};

export default ChatApp;
