import "./main.css";

import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ChatApp() {
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Получаем все сообщения при загрузке страницы
  useEffect(() => {
    fetchMessages();
  }, []);

  async function fetchMessages() {
    try {
      const response = await axios.get('http://localhost:8000/api/messages/');
      setMessages(response.data);
    } catch (error) {
      console.error(error);
    }
  }

  async function handleSubmit(event) {
    event.preventDefault();
    setIsLoading(true);
    setNewMessage('');
    try {
      const response = await axios.post(
        'http://localhost:8000/api/messages/',
        { text: newMessage },
        { headers: { 'Content-Type': 'application/json' } }
      );
      
      setIsLoading(false);
      setMessages([...messages, { text: newMessage, processed_text: response.data.processed_text }]);
      
    } catch (error) {
      console.error(error);
      setIsLoading(false);
    }
  }
  const containerStyle = {
    whiteSpace: 'pre-wrap', // Эффект, аналогичный <pre>
    wordWrap: 'break-word', // Перенос слов
    // Вы можете добавить другие стили сюда
  };
  

  return (
    <div>
      <h1>Мессенджер</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Введите сообщение..."
        />
        <button type="submit">Отправить</button>
      </form>
      {isLoading && <span>Загрузка...</span>}
      {messages.length === 0 ? (
        <p>Нет сообщений</p>
        
      ) : (
        messages.map((message, index) => (
          
          <div key={index}>
            <p>{message.text}</p>
            
            <div style={containerStyle}>{message.processed_text}</div>
          </div>
        ))
      )}
    </div>
  );
}

export default ChatApp;
